def unit_conversion(unit_input, unit_output):
	return unit_table[units[unit_input]][units[unit_output]]

def area_rectangle(length, width, unit_length, unit_width, unit_final):
	unit = 'm'

	length *= unit_conversion(unit_length, unit_final)
	width *= unit_conversion(unit_width, unit_final)

	area = length * width
	return area


def main():
	while True:
		try: length = float(input('Length of the rectangle: '))
		except ValueError:
			print('Invalid input. Enter a valid value for length')
		else: break

	while True:
		unit_length = input('Unit for length: ')
		if unit_length not in units: print('Invalid input. Enter a valid unit for length')
		else: break

	while True:
		try: width = float(input('Width of the rectangle: '))
		except ValueError:
			print('Invalid input. Enter a valid value for width')
		else: break
	
	while True:
		unit_width = input('Unit for width: ')
		if unit_width not in units: print('Invalid input. Enter a valid unit for width')
		else: break

	while True:
		unit_final = input('Unit for area of the rectangle: ')
		if unit_final not in units: print('Invalid input. Enter a valid unit for area')
		else: break


	area = area_rectangle(length, width, unit_length, unit_width, unit_final)
	
	print(f'\nThe area of your rectangle is {area:.3f}{unit_final}²')




if __name__ == '__main__':
	main()